import { CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components';
import React, {FC, } from 'react';
import ReactDOM from 'react-dom';
import style  from './ModalOverlay.module.css';

interface ModalOverlayType {
    children: React.ReactNode;
    onClick: () => void;
}

function ModalOverlay ({children, onClick} : ModalOverlayType)  {

    const modalRoot = document.getElementById('modal') as HTMLElement | null;

    const clickOverlayClose = (e: React.MouseEvent) => {
        if(e.target === e.currentTarget) {
            onClick()
        }
    }

    const escClose = (e: KeyboardEvent) => {
        if(e.key === 'Escape' ) {
            onClick()
        }
    }

    React.useEffect(()=> {
        document.addEventListener('keydown', escClose)
            return () => {
                document.removeEventListener('keydown', escClose)
            }
    }, [])



    return modalRoot
    ? ReactDOM.createPortal(
        <div className={`${style.modalOverlay}`} onClick={clickOverlayClose}>
          {children}
        </div>,
        modalRoot
      )
    : null;
};

export default ModalOverlay
